                <view class="col">User</view>
              </uni-col>
              <uni-col :xs="24" :sm="4" :md="4" :lg="4">
                <view class="col">Time</view>
              </uni-col>
              <uni-col :xs="24" :sm="4" :md="4" :lg="4">
                <view class="col">Bet Amount</view>
              </uni-col>
              <uni-col :xs="24" :sm="4" :md="4" :lg="4">
