              v-for="(board, index) in tabs[currentTab].boards"
              :key="index"
              class="board"
              :class="'item-' + (index + 1)"
            >
              <uni-col :xs="24" :sm="4" :md="4" :lg="4">
                <view class="col">{{ board.game }}</view>
              </uni-col>
              <uni-col :xs="24" :sm="4" :md="4" :lg="4">
                <view class="col">{{ board.user }}</view>
              </uni-col>
              <uni-col :xs="24" :sm="4" :md="4" :lg="4">
                <view class="col">{{ board.time }}</view>
              </uni-col>
              <uni-col :xs="24" :sm="4" :md="4" :lg="4">
                <view class="col">{{ board.bet }}</view>
              </uni-col>
              <uni-col :xs="24" :sm="4" :md="4" :lg="4">
                <view class="col">{{ board.multiplier }}</view>
              </uni-col>
              <uni-col :xs="24" :sm="4" :md="4" :lg="4">
                <view class="col">{{ board.payout }}</view>
